package practiceJava;

public class WhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		         //     0        1      2        3
		String[] a= {"Reema","Isha","Payal","Shweta"};
		
		int i=10;  //  0-1-2-3-4
		while(i<9) 
		{
			System.out.println(a[i]);   //Exception-Array Out of index for 4th index.
			i++;
			
		}
		
		
		
		
		
		

	}

}
